        /// &lt;head&gt;
        /// &lt;script type="text/javascript" src="launchhelp.js"&gt;&lt;/script&gt;
        /// &lt;script type="text/javascript" src="pdf.js"&gt;&lt;/script&gt;
        /// &lt;title&gt;niScope_init&lt;/title&gt;
