        /// &lt;title&gt; niScope_ConfigureTriggerEdge&lt;/title&gt;
        /// &lt;link rel="STYLESHEET" type="print/css" href="print.css"&gt;
        /// &lt;link rel="STYLESHEET" type="text/css" href="styles.css"&gt;
        /// &lt;/head&gt;
        /// &lt;body bgcolor="#ffffff"&gt;
        /// &lt;table class="Borderless"&gt;&lt;tr&gt;&lt;td class="paramName"&gt;level&lt;/td&gt;
